import React, { useEffect, useRef } from 'react';
import { Terminal as XTerm } from 'xterm';
import { FitAddon } from 'xterm-addon-fit';
import 'xterm/css/xterm.css';
import { Box, Typography } from '@mui/material';
import { useProject } from '../context/ProjectContext';

const Terminal = () => {
  const terminalRef = useRef(null);
  const xtermRef = useRef(null);
  const fitAddonRef = useRef(null);
  const { executionOutput, isExecuting } = useProject();

  useEffect(() => {
    // Initialize xterm
    xtermRef.current = new XTerm({
      cursorBlink: false,
      fontSize: 14,
      fontFamily: 'Menlo, Monaco, "Courier New", monospace',
      theme: {
        background: '#1e1e1e',
        foreground: '#cccccc',
        cursor: '#cccccc',
        black: '#000000',
        red: '#cd3131',
        green: '#0dbc79',
        yellow: '#e5e510',
        blue: '#2472c8',
        magenta: '#bc3fbc',
        cyan: '#11a8cd',
        white: '#e5e5e5',
        brightBlack: '#666666',
        brightRed: '#f14c4c',
        brightGreen: '#23d18b',
        brightYellow: '#f5f543',
        brightBlue: '#3b8eea',
        brightMagenta: '#d670d6',
        brightCyan: '#29b8db',
        brightWhite: '#e5e5e5'
      },
      rows: 20
    });

    fitAddonRef.current = new FitAddon();
    xtermRef.current.loadAddon(fitAddonRef.current);
    xtermRef.current.open(terminalRef.current);
    fitAddonRef.current.fit();

    // Welcome message
    xtermRef.current.writeln('\x1b[36m╔════════════════════════════════════════╗\x1b[0m');
    xtermRef.current.writeln('\x1b[36m║   Collaborative IDE Terminal v1.0     ║\x1b[0m');
    xtermRef.current.writeln('\x1b[36m╚════════════════════════════════════════╝\x1b[0m');
    xtermRef.current.writeln('');
    xtermRef.current.writeln('\x1b[32mReady to execute code...\x1b[0m');
    xtermRef.current.writeln('\x1b[90mPress Ctrl+Enter in the editor to run your code.\x1b[0m');
    xtermRef.current.writeln('');

    // Handle resize
    const handleResize = () => {
      if (fitAddonRef.current && xtermRef.current) {
        try {
          fitAddonRef.current.fit();
        } catch (error) {
          console.error('Error fitting terminal:', error);
        }
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      if (xtermRef.current) {
        try {
          xtermRef.current.dispose();
        } catch (error) {
          console.error('Error disposing terminal:', error);
        }
      }
    };
  }, []);

  useEffect(() => {
    if (!xtermRef.current) return;

    if (isExecuting) {
      xtermRef.current.clear();
      xtermRef.current.writeln('\x1b[33m⟳ Executing code...\x1b[0m');
      xtermRef.current.writeln('');
    } else if (executionOutput) {
      xtermRef.current.clear();

      // Status indicator
      const statusColor = executionOutput.status === 'completed' ? '32' : '31';
      const statusText = executionOutput.status === 'completed' ? '✓' : '✗';
      xtermRef.current.writeln(`\x1b[${statusColor}m${statusText} Execution ${executionOutput.status}\x1b[0m`);
      xtermRef.current.writeln('');

      // Output
      if (executionOutput.output?.stdout) {
        xtermRef.current.writeln('\x1b[36m[Output]\x1b[0m');
        xtermRef.current.writeln(executionOutput.output.stdout);
        xtermRef.current.writeln('');
      }

      // Errors
      if (executionOutput.output?.stderr) {
        xtermRef.current.writeln('\x1b[31m[Error]\x1b[0m');
        xtermRef.current.writeln(executionOutput.output.stderr);
        xtermRef.current.writeln('');
      }

      if (executionOutput.output?.error) {
        xtermRef.current.writeln('\x1b[31m[System Error]\x1b[0m');
        xtermRef.current.writeln(executionOutput.output.error);
        xtermRef.current.writeln('');
      }

      if (executionOutput.output?.compileOutput) {
        xtermRef.current.writeln('\x1b[33m[Compilation]\x1b[0m');
        xtermRef.current.writeln(executionOutput.output.compileOutput);
        xtermRef.current.writeln('');
      }

      // Performance metrics
      if (executionOutput.executionTime !== undefined) {
        xtermRef.current.writeln(
          `\x1b[90mExecution time: ${executionOutput.executionTime}ms\x1b[0m`
        );
      }

      if (executionOutput.memoryUsed) {
        xtermRef.current.writeln(
          `\x1b[90mMemory used: ${(executionOutput.memoryUsed / 1024).toFixed(2)} KB\x1b[0m`
        );
      }

      // Mock mode indicator
      if (executionOutput.mock) {
        xtermRef.current.writeln('');
        xtermRef.current.writeln('\x1b[33m⚠ Running in mock mode - Judge0 API not configured\x1b[0m');
      }

      xtermRef.current.writeln('');
      xtermRef.current.writeln('\x1b[90m' + '─'.repeat(50) + '\x1b[0m');
    }
  }, [executionOutput, isExecuting]);

  return (
    <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column', bgcolor: '#1e1e1e' }}>
      <Box
        sx={{
          p: 1,
          borderBottom: '1px solid #2d2d2d',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}
      >
        <Typography variant="subtitle2" sx={{ color: '#9e9e9e', fontWeight: 500 }}>
          TERMINAL
        </Typography>
      </Box>
      <Box
        ref={terminalRef}
        sx={{
          flexGrow: 1,
          p: 1,
          bgcolor: '#1e1e1e',
          '& .xterm': { height: '100%' }
        }}
      />
    </Box>
  );
};

export default Terminal;
