import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Divider,
  IconButton,
  Avatar,
  Dialog,
  DialogTitle,
  DialogContent,
  ListItemAvatar,
  useMediaQuery,
  useTheme,
  AppBar,
  Toolbar
} from '@mui/material';
import {
  Send as SendIcon,
  AttachFile as AttachFileIcon,
  Image as ImageIcon,
  Videocam as VideoIcon,
  Mic as MicIcon,
  People as PeopleIcon,
  Close as CloseIcon,
  ArrowBack as ArrowBackIcon
} from '@mui/icons-material';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { getFileUrl } from '../utils/api';
import io from 'socket.io-client';

const Chat = () => {
  const { user } = useAuth();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [newChatName, setNewChatName] = useState('');
  const [participants, setParticipants] = useState([]);
  const [showParticipants, setShowParticipants] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileInputRef] = useState(useRef());
  const [selectedImage, setSelectedImage] = useState(null);
  const [showImageModal, setShowImageModal] = useState(false);
  const messagesEndRef = useRef();
  const socketRef = useRef();

  useEffect(() => {
    fetchChats();
    const SOCKET_URL = process.env.REACT_APP_API_URL || 'https://chat-app-backend-6e9b.onrender.com';
    socketRef.current = io(SOCKET_URL);

    return () => {
      socketRef.current.disconnect();
    };
  }, []);

  useEffect(() => {
    if (selectedChat) {
      fetchMessages(selectedChat._id);
      fetchParticipants(selectedChat._id);
      socketRef.current.emit('join', selectedChat._id);
    }

    return () => {
      if (selectedChat) {
        socketRef.current.emit('leave', selectedChat._id);
      }
    };
  }, [selectedChat, isMobile]);

  useEffect(() => {
    socketRef.current.on('message', (message) => {
      setMessages((prev) => [...prev, message]);
    });
  }, []);

  const fetchChats = async () => {
    try {
      const res = await axios.get('/api/chats');
      setChats(res.data);
    } catch (error) {
      console.error('Error fetching chats:', error);
    }
  };

  const fetchMessages = async (chatId) => {
    try {
      const res = await axios.get(`/api/chats/${chatId}`);
      setMessages(res.data.messages);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const fetchParticipants = async (chatId) => {
    try {
      const res = await axios.get(`/api/chats/${chatId}/participants`);
      setParticipants(res.data);
    } catch (error) {
      console.error('Error fetching participants:', error);
    }
  };

  const handleCreateChat = async () => {
    try {
      await axios.post('/api/chats', { name: newChatName });
      setNewChatName('');
      fetchChats();
    } catch (error) {
      console.error('Error creating chat:', error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedFile) return;

    try {
      let res;
      if (selectedFile) {
        // Send media message
        const formData = new FormData();
        formData.append('file', selectedFile);
        if (newMessage.trim()) {
          formData.append('caption', newMessage);
        }

        res = await axios.post(`/api/chats/${selectedChat._id}/media`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        setMessages((prev) => [...prev, res.data]);
        setSelectedFile(null);
        setNewMessage('');
      } else {
        // Send text message
        res = await axios.post(`/api/chats/${selectedChat._id}/messages`, {
          content: newMessage
        });
        setMessages((prev) => [...prev, res.data]);
        setNewMessage('');
      }

      socketRef.current.emit('message', {
        chatId: selectedChat._id,
        message: res.data
      });
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const removeSelectedFile = () => {
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleImageClick = (imageUrl, caption) => {
    setSelectedImage({ url: imageUrl, caption });
    setShowImageModal(true);
  };

  const closeImageModal = () => {
    setShowImageModal(false);
    setSelectedImage(null);
  };

  const getFileIcon = (messageType) => {
    switch (messageType) {
      case 'image':
        return <ImageIcon />;
      case 'video':
        return <VideoIcon />;
      case 'audio':
        return <MicIcon />;
      default:
        return <AttachFileIcon />;
    }
  };

  const renderMessage = (message) => {
    const isOwnMessage = message.sender._id === user._id;

    return (
      <Box
        key={message._id}
        sx={{
          display: 'flex',
          justifyContent: isOwnMessage ? 'flex-end' : 'flex-start',
          mb: 2,
          px: isMobile ? 1 : 2
        }}
      >
        <Box
          sx={{
            maxWidth: isMobile ? '85%' : '70%',
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          {/* Sender name */}
          <Typography 
            variant="caption" 
            sx={{ 
              mb: 0.5,
              fontWeight: 'bold',
              color: isOwnMessage ? 'primary.main' : 'text.secondary'
            }}
          >
            {isOwnMessage ? 'Tú' : message.sender.username}
          </Typography>

          {/* Message content */}
          <Box
            sx={{
              bgcolor: isOwnMessage ? 'primary.main' : 'grey.200',
              color: isOwnMessage ? 'white' : 'text.primary',
              p: message.messageType === 'image' ? 0 : 1.5,
              borderRadius: 2,
              boxShadow: 1,
              overflow: 'hidden'
            }}
          >
            {message.messageType === 'text' ? (
              <Typography variant="body1">
                {message.content}
              </Typography>
            ) : message.messageType === 'image' ? (
              <Box>
                {/* WhatsApp-style image container */}
                <Box
                  sx={{
                    position: 'relative',
                    width: '100%',
                    maxWidth: isMobile ? '250px' : '300px',
                    borderRadius: '8px 8px 0 0',
                    overflow: 'hidden',
                    cursor: 'pointer',
                    transition: 'transform 0.2s ease-in-out',
                    '&:hover': {
                      transform: 'scale(1.02)'
                    }
                  }}
                  onClick={() => handleImageClick(message.fileUrl, message.content)}
                >
                  <img 
                    src={getFileUrl(message.fileUrl)} 
                    alt=""
                    style={{ 
                      width: '100%',
                      height: 'auto',
                      display: 'block',
                      objectFit: 'cover'
                    }} 
                  />
                  {/* WhatsApp-style image overlay on hover */}
                  <Box
                    sx={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'rgba(0,0,0,0.1)',
                      opacity: 0,
                      transition: 'opacity 0.2s ease-in-out',
                      '&:hover': {
                        opacity: 1
                      }
                    }}
                  />
                </Box>

                {/* Caption below image (like WhatsApp) */}
                {message.content && (
                  <Box sx={{ p: 1.5, pt: 1 }}>
                    <Typography variant="body2">
                      {message.content}
                    </Typography>
                  </Box>
                )}
              </Box>
            ) : message.messageType === 'video' ? (
              <Box>
                <video 
                  controls 
                  style={{ 
                    maxWidth: '100%', 
                    maxHeight: isMobile ? '150px' : '200px', 
                    borderRadius: '8px',
                    display: 'block'
                  }}
                >
                  <source src={getFileUrl(message.fileUrl)} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
                {message.content && (
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    {message.content}
                  </Typography>
                )}
                <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                  {getFileIcon(message.messageType)}
                  <Typography variant="caption" sx={{ ml: 0.5 }}>
                    {message.fileName}
                  </Typography>
                </Box>
              </Box>
            ) : message.messageType === 'audio' ? (
              <Box>
                <audio controls style={{ width: '100%' }}>
                  <source src={getFileUrl(message.fileUrl)} type="audio/mpeg" />
                  Your browser does not support the audio tag.
                </audio>
                {message.content && (
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    {message.content}
                  </Typography>
                )}
                <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                  {getFileIcon(message.messageType)}
                  <Typography variant="caption" sx={{ ml: 0.5 }}>
                    {message.fileName}
                  </Typography>
                </Box>
              </Box>
            ) : (
              <Box>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  {getFileIcon(message.messageType)}
                  <Typography variant="body2" sx={{ ml: 0.5 }}>
                    {message.fileName}
                  </Typography>
                </Box>
                {message.content && (
                  <Typography variant="body2">
                    {message.content}
                  </Typography>
                )}
              </Box>
            )}
          </Box>

          {/* Message time */}
          <Typography 
            variant="caption" 
            sx={{ 
              mt: 0.5,
              opacity: 0.7,
              alignSelf: isOwnMessage ? 'flex-end' : 'flex-start'
            }}
          >
            {new Date(message.createdAt).toLocaleTimeString()}
          </Typography>
        </Box>
      </Box>
    );
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Mobile layout
  if (isMobile) {
    return (
      <Box sx={{ height: 'calc(100vh - 64px)', display: 'flex', flexDirection: 'column' }}>
        {selectedChat ? (
          // Chat view
          <>
            {/* Chat header */}
            <AppBar position="static" color="default" elevation={1}>
              <Toolbar>
                <IconButton
                  edge="start"
                  onClick={() => setSelectedChat(null)}
                  sx={{ mr: 2 }}
                >
                  <ArrowBackIcon />
                </IconButton>
                <Typography variant="h6" sx={{ flexGrow: 1 }}>
                  {selectedChat.name}
                </Typography>
                <IconButton onClick={() => setShowParticipants(true)}>
                  <PeopleIcon />
                </IconButton>
              </Toolbar>
            </AppBar>

            {/* Messages area */}
            <Box sx={{ flexGrow: 1, overflow: 'auto', bgcolor: '#f5f5f5' }}>
              {messages.map(renderMessage)}
              <div ref={messagesEndRef} />
            </Box>

            {/* Message input */}
            <Box sx={{ p: 1, bgcolor: 'background.paper', borderTop: 1, borderColor: 'divider' }}>
              {/* Selected file preview */}
              {selectedFile && (
                <Box sx={{ mb: 1, p: 1, bgcolor: 'grey.100', borderRadius: 1 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      {getFileIcon(selectedFile.type.startsWith('image/') ? 'image' : 
                                  selectedFile.type.startsWith('video/') ? 'video' : 'audio')}
                      <Typography variant="body2" sx={{ ml: 1 }}>
                        {selectedFile.name}
                      </Typography>
                    </Box>
                    <IconButton size="small" onClick={removeSelectedFile}>
                      <CloseIcon />
                    </IconButton>
                  </Box>
                </Box>
              )}

              <Box component="form" onSubmit={handleSendMessage}>
                <Box sx={{ display: 'flex', gap: 1, alignItems: 'flex-end' }}>
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileSelect}
                    accept="image/*,video/*,audio/*"
                    style={{ display: 'none' }}
                  />
                  <IconButton 
                    onClick={() => fileInputRef.current?.click()}
                    color="primary"
                  >
                    <AttachFileIcon />
                  </IconButton>
                  <TextField
                    fullWidth
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                    multiline
                    maxRows={3}
                    size="small"
                  />
                  <Button
                    type="submit"
                    variant="contained"
                    disabled={!newMessage.trim() && !selectedFile}
                    sx={{ minWidth: 'auto', px: 2 }}
                  >
                    <SendIcon />
                  </Button>
                </Box>
              </Box>
            </Box>
          </>
        ) : (
          // Chat list view
          <>
            <Box sx={{ p: 2, bgcolor: 'background.paper', borderBottom: 1, borderColor: 'divider' }}>
              <TextField
                fullWidth
                label="New Chat Name"
                value={newChatName}
                onChange={(e) => setNewChatName(e.target.value)}
                size="small"
                sx={{ mb: 1 }}
              />
              <Button
                fullWidth
                variant="contained"
                onClick={handleCreateChat}
                disabled={!newChatName.trim()}
              >
                Create Chat
              </Button>
            </Box>

            <Box sx={{ flexGrow: 1, overflow: 'auto' }}>
              <List>
                {chats.map((chat) => (
                  <ListItem
                    key={chat._id}
                    button
                    selected={selectedChat?._id === chat._id}
                    onClick={() => setSelectedChat(chat)}
                    sx={{ borderBottom: 1, borderColor: 'divider' }}
                  >
                    <ListItemText 
                      primary={chat.name}
                      secondary={`${chat.participants?.length || 0} participants`}
                    />
                  </ListItem>
                ))}
              </List>
            </Box>
          </>
        )}

        {/* Participants Dialog */}
        <Dialog 
          open={showParticipants} 
          onClose={() => setShowParticipants(false)}
          fullScreen={isMobile}
        >
          <DialogTitle>
            {isMobile && (
              <IconButton
                onClick={() => setShowParticipants(false)}
                sx={{ position: 'absolute', right: 8, top: 8 }}
              >
                <CloseIcon />
              </IconButton>
            )}
            Chat Participants
          </DialogTitle>
          <DialogContent>
            <List>
              {participants.map((participant) => (
                <ListItem key={participant._id}>
                  <ListItemAvatar>
                    <Avatar>{participant.username.charAt(0).toUpperCase()}</Avatar>
                  </ListItemAvatar>
                  <ListItemText 
                    primary={participant.username}
                    secondary={participant.email}
                  />
                </ListItem>
              ))}
            </List>
          </DialogContent>
        </Dialog>

        {/* Full-screen Image Modal */}
        <Dialog 
          open={showImageModal} 
          onClose={closeImageModal}
          fullScreen={isMobile}
          PaperProps={{
            sx: {
              bgcolor: 'rgba(0,0,0,0.9)',
              boxShadow: 'none',
              borderRadius: 0,
              m: 0,
              height: '100vh',
              width: '100vw'
            }
          }}
        >
          <Box
            sx={{
              position: 'relative',
              height: '100vh',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center'
            }}
          >
            {/* Close button */}
            <IconButton
              onClick={closeImageModal}
              sx={{
                position: 'absolute',
                top: 16,
                right: 16,
                color: 'white',
                bgcolor: 'rgba(0,0,0,0.5)',
                '&:hover': {
                  bgcolor: 'rgba(0,0,0,0.7)'
                }
              }}
            >
              <CloseIcon />
            </IconButton>

            {/* Image */}
            {selectedImage && (
              <img
                src={getFileUrl(selectedImage.url)}
                alt=""
                style={{
                  maxWidth: '100%',
                  maxHeight: '80vh',
                  objectFit: 'contain'
                }}
              />
            )}

            {/* Caption */}
            {selectedImage?.caption && (
              <Box
                sx={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  right: 0,
                  bgcolor: 'rgba(0,0,0,0.7)',
                  color: 'white',
                  p: 2,
                  textAlign: 'center'
                }}
              >
                <Typography variant="body1">
                  {selectedImage.caption}
                </Typography>
              </Box>
            )}
          </Box>
        </Dialog>
      </Box>
    );
  }

  // Desktop layout
  return (
    <Container maxWidth="lg" sx={{ mt: 4, height: 'calc(100vh - 100px)' }}>
      <Grid container spacing={2} sx={{ height: '100%' }}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Box sx={{ mb: 2 }}>
              <TextField
                fullWidth
                label="New Chat Name"
                value={newChatName}
                onChange={(e) => setNewChatName(e.target.value)}
                sx={{ mb: 1 }}
              />
              <Button
                fullWidth
                variant="contained"
                onClick={handleCreateChat}
                disabled={!newChatName.trim()}
              >
                Create Chat
              </Button>
            </Box>
            <Divider sx={{ my: 2 }} />
            <List>
              {chats.map((chat) => (
                <ListItem
                  key={chat._id}
                  button
                  selected={selectedChat?._id === chat._id}
                  onClick={() => setSelectedChat(chat)}
                >
                  <ListItemText 
                    primary={chat.name}
                    secondary={`${chat.participants?.length || 0} participants`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', flexDirection: 'column' }}>
            {selectedChat ? (
              <>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h6" sx={{ flexGrow: 1 }}>
                    {selectedChat.name}
                  </Typography>
                  <IconButton onClick={() => setShowParticipants(true)}>
                    <PeopleIcon />
                  </IconButton>
                </Box>

                <Box sx={{ flexGrow: 1, overflow: 'auto', mb: 2 }}>
                  {messages.map(renderMessage)}
                  <div ref={messagesEndRef} />
                </Box>

                {/* Selected file preview */}
                {selectedFile && (
                  <Box sx={{ mb: 2, p: 1, bgcolor: 'grey.100', borderRadius: 1 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        {getFileIcon(selectedFile.type.startsWith('image/') ? 'image' : 
                                    selectedFile.type.startsWith('video/') ? 'video' : 'audio')}
                        <Typography variant="body2" sx={{ ml: 1 }}>
                          {selectedFile.name}
                        </Typography>
                      </Box>
                      <IconButton size="small" onClick={removeSelectedFile}>
                        <CloseIcon />
                      </IconButton>
                    </Box>
                  </Box>
                )}

                <Box component="form" onSubmit={handleSendMessage}>
                  <Grid container spacing={1}>
                    <Grid item>
                      <input
                        type="file"
                        ref={fileInputRef}
                        onChange={handleFileSelect}
                        accept="image/*,video/*,audio/*"
                        style={{ display: 'none' }}
                      />
                      <IconButton 
                        onClick={() => fileInputRef.current?.click()}
                        color="primary"
                      >
                        <AttachFileIcon />
                      </IconButton>
                    </Grid>
                    <Grid item xs>
                      <TextField
                        fullWidth
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type a message..."
                        multiline
                        maxRows={3}
                      />
                    </Grid>
                    <Grid item>
                      <Button
                        type="submit"
                        variant="contained"
                        endIcon={<SendIcon />}
                        disabled={!newMessage.trim() && !selectedFile}
                      >
                        Send
                      </Button>
                    </Grid>
                  </Grid>
                </Box>
              </>
            ) : (
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  height: '100%'
                }}
              >
                <Typography variant="h6" color="text.secondary">
                  Select a chat to start messaging
                </Typography>
              </Box>
            )}
          </Paper>
        </Grid>
      </Grid>

      {/* Participants Dialog */}
      <Dialog open={showParticipants} onClose={() => setShowParticipants(false)}>
        <DialogTitle>Chat Participants</DialogTitle>
        <DialogContent>
          <List>
            {participants.map((participant) => (
              <ListItem key={participant._id}>
                <ListItemAvatar>
                  <Avatar>{participant.username.charAt(0).toUpperCase()}</Avatar>
                </ListItemAvatar>
                <ListItemText 
                  primary={participant.username}
                  secondary={participant.email}
                />
              </ListItem>
            ))}
          </List>
        </DialogContent>
      </Dialog>

      {/* Full-screen Image Modal */}
      <Dialog 
        open={showImageModal} 
        onClose={closeImageModal}
        maxWidth={false}
        PaperProps={{
          sx: {
            bgcolor: 'rgba(0,0,0,0.9)',
            boxShadow: 'none',
            borderRadius: 0,
            m: 0,
            height: '100vh',
            width: '100vw'
          }
        }}
      >
        <Box
          sx={{
            position: 'relative',
            height: '100vh',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center'
          }}
        >
          {/* Close button */}
          <IconButton
            onClick={closeImageModal}
            sx={{
              position: 'absolute',
              top: 16,
              right: 16,
              color: 'white',
              bgcolor: 'rgba(0,0,0,0.5)',
              '&:hover': {
                bgcolor: 'rgba(0,0,0,0.7)'
              }
            }}
          >
            <CloseIcon />
          </IconButton>

          {/* Image */}
          {selectedImage && (
            <img
              src={getFileUrl(selectedImage.url)}
              alt=""
              style={{
                maxWidth: '100%',
                maxHeight: '80vh',
                objectFit: 'contain'
              }}
            />
          )}

          {/* Caption */}
          {selectedImage?.caption && (
            <Box
              sx={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                bgcolor: 'rgba(0,0,0,0.7)',
                color: 'white',
                p: 2,
                textAlign: 'center'
              }}
            >
              <Typography variant="body1">
                {selectedImage.caption}
              </Typography>
            </Box>
          )}
        </Box>
      </Dialog>
    </Container>
  );
};

export default Chat;
